import React from "react";
import { getAllAnimals, getAllRegions } from "../../lib/api";

const AnimalList = () => {
  const [state, setState] = React.useState({ animals: [] });
  const [regionState, setRegionState] = React.useState({ regions: [] });

  const fetchAnimalsFromApi = async () => {
    try {
      const res = await getAllAnimals();
      setState({ animals: res.data });
    } catch (err) {
      console.error("An error occured fetching all animals", err);
    }
  };

  const fetchAnimalRegionsFromApi = async () => {
    try {
      const response = await getAllRegions();
      setRegionState({ regions: response.data });
    } catch (error) {
      console.error("Error occured fetching all regions", error);
    }
  };

  React.useEffect(() => {
    fetchAnimalsFromApi();
    fetchAnimalRegionsFromApi();
  }, []);

  console.log(state);

  return <p>Some Text</p>;
};

export default AnimalList;
